import React, { useState } from 'react';
import './CoinList.css';

const CoinList = ({ balance, updateBalance, userCoins, curCoins, updateFunction }) => {
  const [quantity, setQuantity] = useState('');
  const [expandedCategories, setExpandedCategories] = useState({});

  const buyCoin = (coin) => {
    // ... (your buyCoin function logic)
  };

  const sellCoin = (coin) => {
    // ... (your sellCoin function logic)
  };

  function categorizeCoin(coin) {
    // Customize the categorization logic based on your criteria.
    const marketCap = coin.marketcap;

    if (marketCap < 1e9) {
      return 'Newest Artists'; // Coins with market cap less than 1 billion
    } else if (marketCap < 1e10) {
      return 'Top Gainers'; // Coins with market cap between 1 billion and 10 billion
    } else if (marketCap < 1e11) {
      return 'Large Cap'; // Coins with market cap between 10 billion and 100 billion
    } else {
      return 'Most Traded'; // Coins with market cap greater than or equal to 100 billion
    }
  }

  // Replace placeholders with actual artist names, use an array for each category, and include image URLs
  const categorizedCoins = {
    'Newest Artists': [
      { name: 'Drake', image: 'url_to_drake_image' },
      { name: 'Adele', image: 'url_to_adele_image' },
      { name: 'Ed Sheeran', image: 'url_to_ed_sheeran_image' },
      { name: 'Div', image: 'url_to_div_image' },
    ],
    'Top Gainers': [
      { name: 'Kanye', image: 'url_to_kanye_image' },
      { name: 'Ariana Grande', image: 'url_to_ariana_grande_image' },
      { name: 'Taylor Swift', image: 'url_to_taylor_swift_image' },
      { name: 'ArtistX', image: 'url_to_artistx_image' },
    ],
    'Large Cap': [], // Initialize as an empty array for dynamic content
    'Most Traded': [],
  };

  // Dictionary to map artists to their market cap
  const artistMarketCaps = {
    'Drake': 10000000000,
    'Adele': 8000000000,
    'Ed Sheeran': 9500000000,
    'Div': 7500000000,
    'Kanye': 12000000000,
    'Ariana Grande': 11000000000,
    'Taylor Swift': 10500000000,
    'ArtistX': 12500000000,
    'Beyoncé': 8500000000,
    'Justin Bieber': 9000000000,
    'Rihanna': 9500000000,
    'Eminem': 10500000000,
    'Lady Gaga': 10000000000,
    'The Weeknd': 9900000000,
  };

  // Sort artists by market cap and select the top 3 for the 'Large Cap' category
  const largeCapArtists = Object.keys(artistMarketCaps)
    .sort((a, b) => artistMarketCaps[b] - artistMarketCaps[a])
    .map((name) => ({ name, image: `url_to_${name}_image` }));

  // Update 'Large Cap' category with the top 3 artists
  categorizedCoins['Large Cap'] = largeCapArtists;


  const timeOnPlatform = {
    'Drake': 100,
    'Adele': 365,
    'Ed Sheeran': 485,
    'Div': 234,
    'Kanye': 128,
    'Ariana Grande': 236,
    'Taylor Swift': 1934,
    'ArtistX': 423,
    'Beyoncé': 1023,
    'Justin Bieber': 24,
    'Rihanna': 191,
    'Eminem': 10500000000,
    'Lady Gaga': 782,
    'The Weeknd': 237,
  };

  // Sort artists by market cap and select the top 3 for the 'Large Cap' category
  const newestArtists = Object.keys(timeOnPlatform)
    .sort((a, b) => artistMarketCaps[a] - artistMarketCaps[b])
    .map((name) => ({ name, image: `url_to_${name}_image` }));

  // Update 'Large Cap' category with the top 3 artists
  categorizedCoins['Newest Artists'] = newestArtists;

  const buys = {
    'Drake': 50,
    'Adele': 30,
    'Ed Sheeran': 45,
    'Div': 20,
    'Kanye': 70,
    'Ariana Grande': 60,
    'Taylor Swift': 80,
    'ArtistX': 55,
    'Beyoncé': 75,
    'Justin Bieber': 40,
    'Rihanna': 70,
    'Eminem': 95,
    'Lady Gaga': 85,
    'The Weeknd': 65,
  };

  const sells = {
    'Drake': 30,
    'Adele': 40,
    'Ed Sheeran': 25,
    'Div': 60,
    'Kanye': 25,
    'Ariana Grande': 35,
    'Taylor Swift': 15,
    'ArtistX': 40,
    'Beyoncé': 30,
    'Justin Bieber': 45,
    'Rihanna': 20,
    'Eminem': 40,
    'Lady Gaga': 55,
    'The Weeknd': 35,
  };

  // Combine buys and sells to calculate total trades
  const trades = {};
  for (const artist in buys) {
    trades[artist] = buys[artist] + sells[artist];
  }

  // Sort artists by the total number of trades
  const mostTradedArtists = Object.keys(trades)
    .sort((a, b) => trades[b] - trades[a])
    .map((name) => ({ name, image: `url_to_${name}_image` }));

  // Update 'Most Traded' category with the sorted artists
  categorizedCoins['Most Traded'] = mostTradedArtists;

  const coins = {
    'Drake': 10,
    'Adele': 15,
    'Ed Sheeran': 16,
    'Div': 14,
    'ArtistX': 22,
  };

  const toggleCategoryExpansion = (category) => {
    setExpandedCategories((prevExpanded) => ({
      ...prevExpanded,
      [category]: !prevExpanded[category],
    }));
  };

  return (
    <div className="coin-list">
      <h2>Available Coins</h2>
      {Object.entries(categorizedCoins).map(([category, artistsInCategory]) => (
        <div key={category}>
          <h3>
            {category} - Top 3
          </h3>
          <button onClick={() => toggleCategoryExpansion(category)}>
            {expandedCategories[category] ? 'See Less' : 'See All'}
          </button>
          <h5></h5>
          {expandedCategories[category]
            ? artistsInCategory.map((artist, index) => (
                <div key={index} className="coin-item">
                  <img src={artist.image} alt={artist.name} />
                  <input
                    type="text"
                    value={quantity}
                    onChange={(e) => setQuantity(e.target.value)}
                  />
                  <button onClick={() => buyCoin(artist.name)}>Buy</button>
                  <button onClick={() => sellCoin(artist.name)}>Sell</button>
                </div>
              ))
            : artistsInCategory.slice(0, 3).map((artist, index) => (
                <div key={index} className="coin-item">
                  <img src={artist.image} alt={artist.name} />
                  <input
                    type="text"
                    value={quantity}
                    onChange={(e) => setQuantity(e.target.value)}
                  />
                  <button onClick={() => buyCoin(artist.name)}>Buy</button>
                  <button onClick={() => sellCoin(artist.name)}>Sell</button>
                </div>
              ))}
        </div>
      ))}
    </div>
  );
};

export default CoinList;
