import React, { useState } from 'react';
import './CoinList.css';

const CoinList = ({ balance, updateBalance, userCoins, curCoins, updateFunction }) => {
  const [coins, setCoins] = useState(curCoins);
  const [quantity, setQuantity] = useState('');
  const [expandedCategories, setExpandedCategories] = useState({});

  const buyCoin = (coin) => {
    // ... (same as your original code)

    // Update state and reset quantity
    setCoins(updatedCoins);
    updateFunction(updatedCoins);
    setQuantity('');
  };

  const sellCoin = (coin) => {
    // ... (same as your original code)

    // Update state and reset quantity
    setCoins(updatedCoins);
    updateFunction(updatedCoins);
    setQuantity('');
  };

  // Categorize the coins
  const categorizedCoins = {
    'Trending Now': [],
    'Top Gainers': [],
    'Large Cap': [],
    'Most Traded': [],
  };

  coins.forEach((coin) => {
    const category = categorizeCoin(coin); // Function to categorize coins

    if (category in categorizedCoins) {
      categorizedCoins[category].push(coin);
    }
  });

  const toggleCategoryExpansion = (category) => {
    setExpandedCategories((prevExpanded) => ({
      ...prevExpanded,
      [category]: !prevExpanded[category],
    }));
  };

  return (
    <div className="coin-list">
      <h2>Available Coins</h2>
      {Object.entries(categorizedCoins).map(([category, coinsInCategory]) => (
        <div key={category}>
          <h3>
            {category} - Top 3
            {coinsInCategory.length > 3 && (
              <button onClick={() => toggleCategoryExpansion(category)}>
                {expandedCategories[category] ? 'See Less' : 'See All'}
              </button>
            )}
          </h3>
          {expandedCategories[category]
            ? coinsInCategory.map((coin, index) => (
                <div key={index} className="coin-item">
                  {/* ... (same coin information as your original code) */}
                  <input
                    type="text"
                    value={quantity}
                    onChange={(e) => setQuantity(e.target.value)}
                  />
                  <button onClick={() => buyCoin(coin)}>Buy</button>
                  <button onClick={() => sellCoin(coin)}>Sell</button>
                </div>
              ))
            : coinsInCategory.slice(0, 3).map((coin, index) => (
                <div key={index} className="coin-item">
                  {/* ... (same coin information as your original code) */}
                  <input
                    type="text"
                    value={quantity}
                    onChange={(e) => setQuantity(e.target.value)}
                  />
                  <button onClick={() => buyCoin(coin)}>Buy</button>
                  <button onClick={() => sellCoin(coin)}>Sell</button>
                </div>
              ))}
        </div>
      ))}
    </div>
  );
};

export default CoinList;
